<?php@session_start();include("../config/connection.php");function menu_page($idpge){	global $db;	$que = $db->query("SELECT `name`,`name_eng` FROM `label_menu` WHERE `id`='$idpge'");	$row = $que->fetch_assoc();    if($_SESSION['lang']=="eng"): return $row['name_eng']; else: return $row['name']; endif;}$kabupaten = $_POST['kabupaten'];echo'<option value="">- Pilih Kota -</option>';$query = $db->query("SELECT `id`,`nama_kota` FROM `ongkir` WHERE `kabupaten`='$kabupaten' ORDER BY `nama_kota` ASC");while($row = $query->fetch_assoc()):	echo '<option value="'.$row['id'].'">'.ucwords($row['nama_kota']).'</option>';endwhile;	?>